import { NONE } from "./none.js";
import { subscribe } from "./subscribe.js";
export const lastItemFrom = (self) => new Promise((resolve, reject) => {
    let lastItem = NONE;
    const sub = subscribe(self, {
        onStart() {
            sub.pull();
        },
        onData(data) {
            lastItem = data;
            sub.pull();
        },
        onEnd(err) {
            if (err) {
                reject(err);
            }
            else if (lastItem === NONE) {
                reject(new Error("source was empty"));
            }
            else {
                resolve(lastItem);
            }
        },
    });
    sub.listen();
});
